mapper.update:
UPDATE sys_user_role
SET role_id = #{role_id}
WHERE user_id = #{id}

mapper.select:
SELECT * FROM sys_role WHERE id IN (
    SELECT role_id FROM sys_user_role WHERE user_id = #{id}
)

SELECT * FROM sys_user
WHERE id IN (
    SELECT user_id FROM sys_user_role
    WHERE role_id = #{id}
)
